pragma solidity ^0.8.17;

import {ITTUDeployer} from "../interfaces/ITTUDeployer.sol";
import {TTUV2BeaconManager} from "./TTUV2BeaconManager.sol";
import {TokenTableUnlockerV2} from "../core/TokenTableUnlockerV2.sol";
import {TTFutureTokenV2} from "../core/TTFutureTokenV2.sol";
import {TTTrackerTokenV2} from "../core/TTTrackerTokenV2.sol";
import {TTUFeeCollector} from "../core/TTUFeeCollector.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/interfaces/IERC20Metadata.sol";
import {BeaconProxy} from "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";
import {Clones} from "@openzeppelin/contracts/proxy/Clones.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

contract TTUDeployer is ITTUDeployer, Ownable {
    TTUV2BeaconManager public immutable beaconManager;
    TTUFeeCollector public feeCollector;
    mapping(string => bool) public registry;

    constructor() Ownable() {
        TokenTableUnlockerV2 unlocker = new TokenTableUnlockerV2();
        TTFutureTokenV2 futureToken = new TTFutureTokenV2();
        TTTrackerTokenV2 trackerToken = new TTTrackerTokenV2();
        beaconManager = new TTUV2BeaconManager(
            address(unlocker),
            address(futureToken),
            address(trackerToken)
        );
        beaconManager.transferOwnership(msg.sender);
        feeCollector = new TTUFeeCollector();
        feeCollector.transferOwnership(msg.sender);
        emit TTUDeployerInitialized(
            address(unlocker),
            address(futureToken),
            address(trackerToken),
            address(beaconManager),
            address(feeCollector)
        );
    }

    function setFeeCollector(TTUFeeCollector feeCollector_) external onlyOwner {
        feeCollector = feeCollector_;
        emit FeeCollectorChanged(address(feeCollector));
    }

    function deployTTSuite(
        IERC20Metadata projectToken,
        string calldata projectId,
        bool disableAutoUpgrade,
        bool allowTransferableFT
    )
        external
        returns (TokenTableUnlockerV2, TTFutureTokenV2, TTTrackerTokenV2)
    {
        if (registry[projectId]) revert AlreadyDeployed();
        registry[projectId] = true;

        TTFutureTokenV2 futureToken;
        TokenTableUnlockerV2 unlocker;
        TTTrackerTokenV2 trackerToken;
        if (disableAutoUpgrade) {
            futureToken = TTFutureTokenV2(
                Clones.clone(beaconManager.futureTokenBeacon().implementation())
            );
            futureToken.initialize(projectToken, allowTransferableFT);
            unlocker = TokenTableUnlockerV2(
                Clones.clone(beaconManager.unlockerBeacon().implementation())
            );
            unlocker.initialize(projectToken, futureToken, this);
            trackerToken = TTTrackerTokenV2(
                Clones.clone(
                    beaconManager.trackerTokenBeacon().implementation()
                )
            );
            trackerToken.initialize(address(unlocker));
        } else {
            futureToken = TTFutureTokenV2(
                address(
                    new BeaconProxy(
                        address(beaconManager.futureTokenBeacon()),
                        abi.encodeWithSelector(
                            TTFutureTokenV2.initialize.selector,
                            projectToken,
                            allowTransferableFT
                        )
                    )
                )
            );
            unlocker = TokenTableUnlockerV2(
                address(
                    new BeaconProxy(
                        address(beaconManager.unlockerBeacon()),
                        abi.encodeWithSelector(
                            TokenTableUnlockerV2.initialize.selector,
                            projectToken,
                            futureToken,
                            this
                        )
                    )
                )
            );
            trackerToken = TTTrackerTokenV2(
                address(
                    new BeaconProxy(
                        address(beaconManager.trackerTokenBeacon()),
                        abi.encodeWithSelector(
                            TTTrackerTokenV2.initialize.selector,
                            address(unlocker)
                        )
                    )
                )
            );
        }

        unlocker.transferOwnership(msg.sender);
        futureToken.setAuthorizedMinterSingleUse(address(unlocker));
        emit TokenTableSuiteDeployed(
            msg.sender,
            projectId,
            address(unlocker),
            address(futureToken),
            address(trackerToken)
        );
        return (unlocker, futureToken, trackerToken);
    }
}
","keccak256":"0x0060ca88331b9f9af958e5c976dee707b0244cac024ce41e5f99a6bdb2a60e87","license":"UNLICENSED"},"contracts/proxy/TTUV2BeaconManager.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.17;

import {UpgradeableBeacon} from "@openzeppelin/contracts/proxy/beacon/UpgradeableBeacon.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title TTUV2BeaconManager
 * @author Jack Xu @ EthSign
 * @dev This contract manages the upgradeable beacons that we use to seamlessly
 * upgrade TokenTableUnlocker, TTFutureToken, and TTTrackerToken on behalf of
 * our users in the future.
 *
 * This contract should be deployed using TTUDeployer.
 */
contract TTUV2BeaconManager is Ownable {
    UpgradeableBeacon public immutable unlockerBeacon;
    UpgradeableBeacon public immutable futureTokenBeacon;
    UpgradeableBeacon public immutable trackerTokenBeacon;

    constructor(
        address unlockerImpl,
        address futureTokenImpl,
        address trackerTokenImpl
    ) {
        unlockerBeacon = new UpgradeableBeacon(unlockerImpl);
        futureTokenBeacon = new UpgradeableBeacon(futureTokenImpl);
        trackerTokenBeacon = new UpgradeableBeacon(trackerTokenImpl);
    }

    function upgradeUnlocker(address newImpl) external onlyOwner {
        unlockerBeacon.upgradeTo(newImpl);
    }

    function upgradeFutureToken(address newImpl) external onlyOwner {
        futureTokenBeacon.upgradeTo(newImpl);
    }

    function upgradePreviewToken(address newImpl) external onlyOwner {
        trackerTokenBeacon.upgradeTo(newImpl);
    }
}
